    console.log(newTours);
    setTours(newTours);
  }
  function refreshTour() {
    setTours(data);
  }
  if(tours.length ) {
    return (
      <div className='refresh'>
        <h1>No Choice  Left...</h1>
        <button className='refBtn' onClick={refreshTour}> Refresh</button>
      </div>
    );

  }

