import React, { useState } from "react";

function Card({ id, image, info, price, name, removeeTour,resetTourr }) {

    const [readmore, setReadMore] = useState(false);
    const description = readmore ? info : `${info.substring(0, 200)}....`;

//  const[tour,removeeTour] = useState('');

    function readmoreHandler() {
        setReadMore(!readmore);
    }


    return (
        <div className="card">
            <img src={image} className="image" alt="img" />
            <div className="tourInfo">
                <div className="tourDetails">
                    <h4 className="tourPrice">{price}</h4>
                    <h4 className="tourName">{name}</h4>
                </div>
                <div className="description">
                    {description}
                    <span className="readMore" onClick={readmoreHandler}>
                        {readmore ? `show less` : `read more`}
                    </span>
                </div>
            </div>
            <button className="btnRed" onClick={() => removeeTour(id)}>
                Not Interested
            </button>
            <button className="btnreseed" onClick={() => resetTour(id)}> Reset</button>
        </div >
    );
};

export default Card;
